
// JSON Data Model
import 'package:flutter/material.dart';

class ShopItem {
  final String id;
  final String name;
  final String imageUrl;
  final double price;

  ShopItem({
    required this.id,
    required this.name,
    required this.imageUrl,
    required this.price,
  });

  factory ShopItem.fromJson(Map<String, dynamic> json) {
    return ShopItem(
      id: json['id'],
      name: json['name'],
      imageUrl: json['imageUrl'],
      price: json['price'],
    );
  }

  Map<String, dynamic> toJson() {
    return {
      'id': id,
      'name': name,
      'imageUrl': imageUrl,
      'price': price,
    };
  }
}


